depStats._queryString:
{[airport]

  beginningOfYear:(string `year$.z.d),".01.01";

  h: hopen `:localhost:24028:admin:admin; 
  historical:(enlist `date) _ h"select from flights where date > .z.d - ",beginningOfYear;

  wtd: distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from (flights,historical) where depAirport=airport, arivTime < .z.p, arivTime > .z.d-((.z.d mod 7)-2);

  mtd: distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from (flights,historical) where depAirport=airport, arivTime < .z.p, arivTime > "d"$(`month$.z.d);

  ytd: distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from (flights,historical) where depAirport=airport, arivTime < .z.p, arivTime > ("D"$(string `year$.z.d),".01.01");

  departing: distinct select sym,arivAirport,"u"$depTime,"u"$arivTime,flightNumber,aircraftType, registration from flights where depAirport=airport;

  todays: distinct select sym,arivAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from flights where depAirport=airport, depTime < .z.p, depTime > "d"$.z.p;

  destinations: distinct (value flip key  5# `num xdesc select num:count time by arivAirport from flights where depAirport=airport)[0];

  airlines: distinct (value flip key  5# `num xdesc select num:count time by sym from flights where depAirport=airport)[0];

  ([]
    property: ("Departures:";"Today";"WTD";"MTD";"YTD";"Most Popular:"; "Destination"; "Airlines");
    propertyValue:("";count todays; count wtd; count mtd; count ytd; ""; destinations;airlines) 
    )
  }

