airportStats._queryString:
{[airport]
 
  toAirport:count distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from flights where arivAirport=airport, depTime < .z.p, depTime > "d"$.z.p;

  fromAirport:count distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from flights where depAirport=airport, depTime < .z.p, depTime > "d"$.z.p;

  totalFlights: count distinct select sym,depAirport,"u"$depTime,"u"$arivTime,flightNumber, aircraftType, registration from flights where depTime < .z.p, depTime > "d"$.z.p;

  percentage: (enlist (fromAirport+toAirport))%totalFlights;

  ([]
    property: enlist ("% of all flights (so far today)");
    propertyValue: enlist  (.Q.fmt[6;1;100*percentage[0]]),"%"
    )
  
  }

