count_landed._queryString:
{[st;ed]
  h: hopen `:localhost:HDBPORT:admin:admin; 
  historical:(enlist `date)_h"select from flights where date within (", (string st), ";",(string ed),")";
  todays:flights;
  HL:count select from historical where status=`LD;
  TL:count select from todays where status=`LD;
  HAL:HL%1+ed-st;
  HC:count select from historical where status=`CD;
  HAC:HC%1+ed-st;
  TC:count select from todays where status=`CD;
  ([Data:`Landed`Cancelled] today:TL,TC;totalhistorical:HL,HC;"i"$averageHistorical:HAL,HAC)
 }

count_each_type._queryString:
{[st;ed] 
  h: hopen `:localhost:HDBPORT:admin:admin; 
  historical:(enlist `date)_h"select from flights where date within (", (string st), ";",(string ed),")";
  todays:flights;
  typetoday:select typecount:count i by aircraftType from distinct select sym,depAirport,depTime,arivTime,arivAirport,flightNumber,aircraftType from todays;
  typehist:select historicalAverage:"i"$((count i)%1+ed-st) by aircraftType from distinct select sym,depAirport,depTime,arivTime,arivAirport,flightNumber,aircraftType from historical;
  0^typetoday uj typehist
 }

count_dep_by_airport._queryString:
arivalcount:select arivCount:count i by airport:arivAirport from (distinct select sym,depAirport,depTime,arivTime,arivAirport,flightNumber,aircraftType from flights where status=`LD);
departurecount:select depCount:count i by airport:depAirport from (distinct select sym,depAirport,depTime,arivTime,arivAirport,flightNumber,aircraftType from flights where status=`DP);
0^(arivalcount uj departurecount)

FlightData._queryString:
todays:select from flights;

intheair:count select by flightNumber from todays where status=`DP;
landed:count select by flightNumber from todays where status=`LD;
noInfo:count select by flightNumber from todays where status=`NA;
cancelled:count select by flightNumber from todays where status=`CD;
flip ([] Departed: enlist intheair; Landed:enlist landed; NA: enlist noInfo; Cancelled: enlist cancelled)

arivdep_single._queryString:
{[sy]
  arivalcount:`airport xcol select todayArivals:count i by arivAirport from flights where status=`LD,arivAirport=sy;
  
  departurecount:`airport xcol select todayDepartures:count i by depAirport from flights where status=`DP,depAirport=sy;
  0^(arivalcount uj departurecount)
 }

Airporttodayandhist._queryString:
{[st;ed]
  h: hopen `:localhost:HDBPORT:admin:admin; 
  historical:(enlist `date)_h"select from flights where date within (", (string st), ";",(string ed),")";
  todays:flights;
  arivhist:`airport xcol select histAvgArivals:((count i)%1+ed-st) by arivAirport from historical where status=`LD;

  dephist:`airport xcol select histAvgDepartures:((count i)%1+ed-st) by depAirport from historical where status=`DP;

    arivalcount:`airport xcol select todayArivals:count i by arivAirport from flights where status=`LD;
  departurecount:`airport xcol select todayDepartures:count i by depAirport from flights where status=`DP;

 0^(arivalcount uj departurecount) uj (arivhist uj dephist)
 }

arivdephistsingle._queryString:
{[st;ed;sy]
  h: hopen `:localhost:HDBPORT:admin:admin; 
  historical:(enlist `date)_h"select from flights where date within (", (string st), ";",(string ed),")";

  harivalcount:`airport xcol select averagehistoricalArivals:((count i)%1+ed-st) by arivAirport from historical where status=`LD,arivAirport=sy;
  
  hdeparturecount:`airport xcol select averagehistoricalDepartures:((count i)%1+ed-st) by depAirport from historical where status=`DP,depAirport=sy;
  0^(harivalcount uj hdeparturecount)
 }
